import React from "react";
//import { css } from '@emotion/react';
import styled from "@emotion/styled";

const Footer = styled.section`
  display: flex;
  background-color: #304F47;
  height: 496px;
  width: 100%;
  color: white;
  display: flex;
  z-index: 1;
  position: relative;
`;

export default function footer() {
  return (
    <Footer>
      <div>
        <h1>Let you have any question, Let us help you!</h1>
        <div>
          <p>
            If you have any questions or would like to book a tour with us,
            please don't hesitate to contact us. <br/><br/>
            Phone: +250 788 123 456 <br />
            Email: info@rwandatourism.com <br />
            Address: Kigali, Rwandabr <br />           
            <br /><br /><br />
            <b>Subscribe to our news paper</b>
            </p>
            <div>
              <form action="">
                <input type="text" />
                <button type="submit">Send</button>
              </form>
            </div>
            <div>
              <div>For the images</div>
              <div><p>Lorem Ipsum is simply dummy text of the printing and typesetting industry.</p></div>
              <div>
                <ul type="none">
                <li>Home</li>
                <li>Testimonials</li>
                <li>Destinations</li>
                <li>Sign In</li>
                <li>About Us</li>
                <li>Packages</li>
                <li>Events</li>
                <li>Gallery</li>
                </ul></div>
            </div>
        </div>
        <div>Copyright Africa Wizzy Safari 2022</div><br />
        <div>Design implemented by <b>@clint360</b></div>
      </div>
    </Footer>
  );
}
